package algorithms.recursion;

public class StringReverse {

    public static void main(String[] args) {
        // This is why recursion in Java is not a good idea - StackOverflowError
        System.out.println(new StringReverse().reverse(""));
    }

    private String reverse(String something) {
        if ((something == null) || something.isEmpty()) {
            return something;
        }
        char lastChar = something.charAt((something.length() - 1));
        return lastChar + reverse(something.substring(0, something.length() - 1));
    }

}
